package com.gqt.corejava.patterns;
import java.util.Scanner;
class B2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in=new Scanner(System.in);
		System.out.println("Enter size: ");
		int n=in.nextInt();
		for(int i=0;i<n;i++)
		{
			System.out.println("*");
		}
	}

}
